import React, { useEffect, useState } from "react";
import Header from "../components/header";
import { useLocation, useNavigate } from "react-router-dom";
import "../index.css";
import { Button, Modal, message } from "antd";

const SearchFlightsPage = () => {
  const [flights, setFlights] = useState([]);
  const [messageText, setMessageText] = useState("");
  const [isMilesMember, setIsMilesMember] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);
  const [useMiles, setUseMiles] = useState(false);
  const location = useLocation();
  const { searchParams, passengers, classType } = location.state || {};
  const userId = localStorage.getItem("userId"); // Kullanıcı ID'sini localStorage'dan al
  const navigate = useNavigate();

  useEffect(() => {
    console.log("Stored User ID:", userId); // userId'nin doğru bir şekilde alınıp alınmadığını kontrol et
    const fetchFlights = async () => {
      if (!searchParams) return;

      const { from, to, startDate, endDate } = searchParams;

      try {
        const response = await fetch(
          `http://localhost:4000/api/flights/search-flights?from=${from}&to=${to}&startDate=${startDate}&endDate=${endDate}&userId=${userId}`
        );
        const data = await response.json();

        if (response.ok) {
          setFlights(data.flights);
          setIsMilesMember(data.isMilesMember);
          if (data.flights.length === 0) {
            setMessageText("No flights found.");
          }
        } else {
          setMessageText(
            data.message || "An error occurred while fetching flights."
          );
        }
      } catch (error) {
        setMessageText("An error occurred while fetching flights.");
      }
    };

    fetchFlights();
  }, [searchParams, userId]);

  const updateFlightCapacity = async () => {
    const flightId = "66646ac1c20d6c4af07167d3"; // test etmek istediğiniz flightId'yi burada belirtin
    const userId = "60d0fe4f5311236168a109ca"; // test etmek istediğiniz userId'yi burada belirtin
    const passengers = 1;
    const useMiles = false;
    const classType = "Economy";

    try {
      const response = await fetch(
        `http://localhost:4000/api/flights/update-capacity/${flightId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ passengers, useMiles, userId, classType }),
        }
      );

      const data = await response.json();
      if (response.ok) {
        console.log("Success:", data);
      } else {
        console.log("Error:", data.message);
      }
    } catch (error) {
      console.log("Error:", error);
    }
  };

  // Bu fonksiyonu çalıştırarak PUT isteğini test edebiliriz
  updateFlightCapacity();

  const showModal = (flight, useMiles = false) => {
    setSelectedFlight(flight);
    let price = useMiles
      ? flight.milesPoints
      : classType === "Economy"
      ? flight.economyPrice
      : flight.businessPrice;
    setTotalPrice(price * passengers);
    setUseMiles(useMiles);
    setIsModalVisible(true);
  };

  const handleOk = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/flights/update-capacity/${selectedFlight._id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ passengers, useMiles, userId, classType }),
        }
      );

      if (response.ok) {
        message.success(
          "Tebrikler, başarılı bir şekilde satın aldınız. OGUZHAN Airlines iyi uçuşlar diler."
        );
        setIsModalVisible(false);
        const updatedFlights = flights.map((flight) =>
          flight._id === selectedFlight._id
            ? { ...flight, capacity: flight.capacity - passengers }
            : flight
        );
        setFlights(updatedFlights);
        navigate("/"); // Ana sayfaya yönlendir
      } else {
        const errorData = await response.json();
        message.error(`Hata: ${errorData.message}`);
      }
    } catch (error) {
      message.error(`Hata: ${error.message}`);
    }
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <Header />
      <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-2xl mt-20">
        {!isMilesMember && (
          <div className="text-center text-red-500 mb-4">
            <p>
              Miles&Smiles üyesi değilsiniz. Avantajlarından faydalanmak ister
              misiniz?
            </p>
            <a href="/signup-miles&smiles" className="text-blue-500 underline">
              Buradan üye olun.
            </a>
          </div>
        )}
        {flights.length > 0 ? (
          flights.map((flight, index) => (
            <div key={index} className="border rounded-lg p-4 mb-4 shadow-md">
              <div className="flex justify-between items-center">
                <div>
                  <span className="font-bold text-lg">
                    Airline ID : {flight.flightCode}
                  </span>
                  <span className="block">
                    {flight.from} - {flight.to}
                  </span>
                </div>
                <div className="text-right">
                  <span className="font-bold text-lg">
                    {new Date(flight.flightDate).toLocaleTimeString([], {
                      hour: "2-digit",
                      minute: "2-digit",
                    })}
                  </span>
                  <span className="block">
                    Flight Duration: {flight.flightDuration}
                  </span>
                  <span className="block">
                    Operated by: {flight.operatedBy}
                  </span>
                </div>
              </div>
              <div className="mt-4">
                <div className="flex justify-between">
                  <div className="flex flex-col items-center">
                    <span className="text-lg font-bold">Economy</span>
                    <span className="text-sm">Per passenger</span>
                    <span className="text-xl font-bold">
                      TRY {flight.economyPrice}
                    </span>
                  </div>
                  <div className="flex flex-col items-center">
                    <span className="text-lg font-bold">Business</span>
                    <span className="text-sm">Per passenger</span>
                    <span className="text-xl font-bold">
                      TRY {flight.businessPrice}
                    </span>
                    <span className="text-sm text-red-500">
                      4 left at this price
                    </span>
                  </div>
                </div>
                <div className="text-right mt-2">
                  <span className="text-blue-500 cursor-pointer">
                    Itinerary details
                  </span>
                </div>
                <div className="flex justify-between mt-2">
                  <Button
                    type="primary"
                    danger
                    onClick={() => showModal(flight)}
                  >
                    Buy Ticket
                  </Button>
                  {isMilesMember && flight.milesPoints > 0 && (
                    <Button
                      type="primary"
                      onClick={() => showModal(flight, true)}
                    >
                      Buy with Miles
                    </Button>
                  )}
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center text-red-500">
            <p>{messageText}</p>
          </div>
        )}
      </div>

      <Modal
        title="Ticket Purchase"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <p>
          Total Price is:{" "}
          {useMiles ? totalPrice + " Miles" : "TRY " + totalPrice}
        </p>
      </Modal>
    </div>
  );
};

export default SearchFlightsPage;
