import React, { useState } from "react";
import Header from "../components/header";

function SignupPage() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
    userType: "user", // Varsayılan değer 'user'
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const [fetchUsername, setFetchUsername] = useState("");

  const handleFetchUser = async () => {
    try {
      const response = await fetch(`/api/users/${fetchUsername}`);
      if (response.ok) {
        const user = await response.json();
        setFormData(user);
      } else {
        console.error("Failed to fetch user");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault(1);
    // Backend API'ye POST isteği gönderme
    try {
      const response = await fetch("http://localhost:4000/api/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log("Response:", data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <Header />
      <div className="mx-auto max-w-xl border rounded-lg p-8 mt-10 border-gray-400">
        <h1 className="text-2xl mb-5 text-center font-bold underline underline-offset-8">
          Sign Up
        </h1>
        <form onSubmit={handleSubmit} className="py-5">
          <div className="mb-4 flex flex-col mx-auto w-[450px]">
            <label className="block text-gray-700" htmlFor="username">
              Username:
            </label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4 flex flex-col mx-auto w-[450px]">
            <label className="block text-gray-700" htmlFor="password">
              Password:
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4 flex flex-col mx-auto w-[450px]">
            <label className="block text-gray-700" htmlFor="email">
              Email:
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4 flex flex-col mx-auto w-[450px]">
            <label className="block text-gray-700" htmlFor="userType">
              User Type:
            </label>
            <select
              id="userType"
              name="userType"
              value={formData.userType}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded"
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <div className="flex justify-center">
            <button
              type="submit"
              className="bg-purple-800 mt-5 text-white py-2 px-3 rounded-md custom-shadow custom-shadow:hover button:active"
            >
              Create your Account
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignupPage;
