import React, { useState, useEffect } from "react";
import Header from "../components/header";
import "../index.css";

const SearchFlightsPage = () => {
  const [searchParams, setSearchParams] = useState({
    from: "",
    to: "",
    startDate: "",
    endDate: "",
  });
  const [flights, setFlights] = useState([]);
  const [message, setMessage] = useState("");
  const [locations, setLocations] = useState({
    fromLocations: [],
    toLocations: [],
  });

  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await fetch(
          "http://localhost:4000/api/flights/locations"
        );
        const data = await response.json();
        if (response.ok) {
          setLocations(data);
        } else {
          console.error(data.message);
        }
      } catch (error) {
        console.error("An error occurred while fetching locations.");
      }
    };

    fetchLocations();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSearchParams({
      ...searchParams,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const response = await fetch(
        `http://localhost:4000/api/flights/search-flights?from=${searchParams.from}&to=${searchParams.to}&startDate=${searchParams.startDate}&endDate=${searchParams.endDate}`
      );

      const data = await response.json();

      if (response.ok) {
        setFlights(data);
        if (data.length === 0) {
          setMessage("No flights found.");
        }
      } else {
        setMessage(
          data.message || "An error occurred while searching flights."
        );
      }
    } catch (error) {
      setMessage("An error occurred while searching flights.");
    }
  };

  return (
    <div>
      <Header />
      <div className="max-w-lg mx-auto mt-10 p-6 bg-white rounded-lg shadow-2xl mt-20">
        <h2 className="text-2xl font-bold mb-6 text-center text-red-500">
          Search Flights
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700">From:</label>
            <select
              name="from"
              value={searchParams.from}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select From Location</option>
              {locations.fromLocations.map((location, index) => (
                <option key={index} value={location}>
                  {location}
                </option>
              ))}
            </select>
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">To:</label>
            <select
              name="to"
              value={searchParams.to}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select To Location</option>
              {locations.toLocations.map((location, index) => (
                <option key={index} value={location}>
                  {location}
                </option>
              ))}
            </select>
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Start Date:</label>
            <input
              type="date"
              name="startDate"
              value={searchParams.startDate}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">End Date:</label>
            <input
              type="date"
              name="endDate"
              value={searchParams.endDate}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 custom-shadow"
          >
            Search
          </button>
        </form>
        {message && (
          <div className="mt-4 text-center text-red-500">
            <p>{message}</p>
          </div>
        )}
      </div>
      <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-2xl">
        {flights.length > 0 && (
          <div>
            {flights.map((flight, index) => (
              <div key={index} className="border rounded-lg p-4 mb-4 shadow-md">
                <div className="flex justify-between items-center">
                  <div>
                    <span className="font-bold text-lg">
                      {flight.flightCode}
                    </span>
                    <span className="block">
                      {flight.from} - {flight.to}
                    </span>
                  </div>
                  <div className="text-right">
                    <span className="font-bold text-lg">
                      {new Date(flight.flightDate).toLocaleTimeString([], {
                        hour: "2-digit",
                        minute: "2-digit",
                      })}
                    </span>
                    <span className="block">
                      Flight Duration: {flight.flightDuration}
                    </span>
                    <span className="block">
                      Operated by: {flight.operatedBy}
                    </span>
                  </div>
                </div>
                <div className="mt-4">
                  <div className="flex justify-between">
                    <div className="flex flex-col items-center">
                      <span className="text-lg font-bold">Economy</span>
                      <span className="text-sm">Per passenger</span>
                      <span className="text-xl font-bold">
                        TRY {flight.economyPrice}
                      </span>
                    </div>
                    <div className="flex flex-col items-center">
                      <span className="text-lg font-bold">Business</span>
                      <span className="text-sm">Per passenger</span>
                      <span className="text-xl font-bold">
                        TRY {flight.businessPrice}
                      </span>
                      <span className="text-sm text-red-500">
                        4 left at this price
                      </span>
                    </div>
                  </div>
                  <div className="text-right mt-2">
                    <span className="text-blue-500 cursor-pointer">
                      Itinerary details
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchFlightsPage;
