import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { Card, List } from "antd";

const SearchFlightsPage = () => {
  const location = useLocation();
  const { searchParams } = location.state;
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/api/flights/search-flights?from=${searchParams.from}&to=${searchParams.to}&startDate=${searchParams.startDate}&endDate=${searchParams.endDate}`
        );

        const data = await response.json();

        if (response.ok) {
          setFlights(data);
        } else {
          console.error(
            data.message || "An error occurred while searching flights."
          );
        }
      } catch (error) {
        console.error("An error occurred while searching flights.", error);
      }
    };

    fetchFlights();
  }, [searchParams]);

  return (
    <div className="container mx-auto px-4 py-10">
      <h2 className="text-2xl font-bold mb-6">Search Results</h2>
      <List
        grid={{ gutter: 16, column: 1 }}
        dataSource={flights}
        renderItem={(flight) => (
          <List.Item>
            <Card title={`${flight.from} - ${flight.to}`}>
              <p>
                <strong>Flight Code:</strong> {flight.flightCode}
              </p>
              <p>
                <strong>Departure Time:</strong>{" "}
                {new Date(flight.flightDate).toLocaleTimeString()}
              </p>
              <p>
                <strong>Arrival Time:</strong>{" "}
                {new Date(flight.arrivalDate).toLocaleTimeString()}
              </p>
              <p>
                <strong>Flight Duration:</strong> {flight.flightDuration}
              </p>
              <p>
                <strong>Operated by:</strong> {flight.operatedBy}
              </p>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default SearchFlightsPage;
