import React, { useState } from "react";
import Header from "../components/header";
import "../index.css";

const SearchFlightsPage = () => {
  const [searchParams, setSearchParams] = useState({
    from: "",
    to: "",
    startDate: "",
    endDate: "",
  });
  const [flights, setFlights] = useState([]);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSearchParams({
      ...searchParams,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const response = await fetch(
        `http://localhost:4000/api/flights/search-flights?from=${searchParams.from}&to=${searchParams.to}&startDate=${searchParams.startDate}&endDate=${searchParams.endDate}`
      );

      const data = await response.json();

      if (response.ok) {
        setFlights(data);
        if (data.length === 0) {
          setMessage("No flights found.");
        }
      } else {
        setMessage(
          data.message || "An error occurred while searching flights."
        );
      }
    } catch (error) {
      setMessage("An error occurred while searching flights.");
    }
  };

  return (
    <div>
      <Header />
      <div className="max-w-lg mx-auto mt-10 p-6 bg-white rounded-lg shadow-2xl mt-20">
        <h2 className="text-2xl font-bold mb-6 text-center text-red-500">
          Search Flights
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700">From:</label>
            <input
              type="text"
              name="from"
              value={searchParams.from}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">To:</label>
            <input
              type="text"
              name="to"
              value={searchParams.to}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Start Date:</label>
            <input
              type="date"
              name="startDate"
              value={searchParams.startDate}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">End Date:</label>
            <input
              type="date"
              name="endDate"
              value={searchParams.endDate}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 custom-shadow"
          >
            Search
          </button>
        </form>
        {message && (
          <div className="mt-4 text-center text-red-500">
            <p>{message}</p>
          </div>
        )}
      </div>
      <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-2xl">
        {flights.length > 0 && (
          <div>
            {flights.map((flight, index) => (
              <div key={index} className="border rounded-lg p-4 mb-4 shadow-md">
                <div className="flex justify-between items-center">
                  <div>
                    <span className="font-bold text-lg">
                      {flight.flightCode}
                    </span>
                    <span className="block">
                      {flight.from} - {flight.to}
                    </span>
                  </div>
                  <div className="text-right">
                    <span className="font-bold text-lg">
                      {new Date(flight.flightDate).toLocaleTimeString()}
                    </span>
                    <span className="block">{flight.flightDuration} mins</span>
                    <span className="block">
                      Operated by: {flight.operatedBy}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchFlightsPage;
