import React, { useState } from "react";
import background from "../images/bg-image.jpeg";
import { Button, Radio, Input, DatePicker } from "antd";

const { Search } = Input;
const { RangePicker } = DatePicker;

const MainPage = () => {
  const [flightType, setFlightType] = useState("roundtrip");

  const handleFlightTypeChange = (e) => {
    setFlightType(e.target.value);
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center"
      style={{ backgroundImage: `url(${background})` }}
    >
      <div className="container mx-auto px-4 py-40 text-center">
        <h1 className="text-4xl font-bold text-white">HELLO</h1>
        <h2 className="text-6xl font-bold text-white mb-8">
          Where do you want to explore?
        </h2>
        <div className="bg-white rounded-lg shadow-2xl p-8">
          <div className="flex justify-center mb-4 space-x-4">
            <Button type="primary" danger>
              Flight
            </Button>
            <Button>Check-in / Manage booking</Button>
            <Button>Flight status</Button>
          </div>
          <div className="flex justify-center items-center gap-5 mb-4">
            <Radio.Group onChange={handleFlightTypeChange} value={flightType}>
              <Radio value="roundtrip">Round Trip</Radio>
              <Radio value="oneway">One Way</Radio>
            </Radio.Group>
            <label className="flex items-center space-x-2">
              <input type="checkbox" />
              <span>Award ticket - Buy a ticket with Miles</span>
            </label>
          </div>
          <div className="flex flex-col-1 md:grid-cols-4 gap-4 mb-4">
            <Input
              className="p-2 border border-gray-300 rounded bg-gray-200"
              placeholder="From"
            />
            <Input
              className="p-2 border border-gray-300 rounded bg-gray-200"
              placeholder="To"
            />
            {flightType === "roundtrip" ? (
              <RangePicker className="w-full p-2 border border-gray-300 rounded bg-gray-200" />
            ) : (
              <DatePicker className="w-full p-2 border border-gray-300 rounded" />
            )}
            <Input
              className="p-2 border border-gray-300 rounded bg-gray-200"
              placeholder="Passengers"
            />
          </div>
          <div className="flex justify-end">
            <Button type="primary" danger>
              Search flights
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
