import React, { useState } from "react";
import logo from "../images/OguzhanAirline.png";
import { Link } from "react-router-dom";

const Header = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      <header className="bg-gray-800 text-white h-[70px] w-full">
        <div className="flex items-center px-4">
          <div className="flex items-center space-x-2">
            <img
              src={logo}
              alt="Oguzhan Airline Logo"
              className="h-[60px] w-[100px] object-cover"
            />
            <Link to={"/"}>
              <span className="font-bold text-[18px]">OGUZHAN AIRLINES </span>
            </Link>
          </div>
          <nav className="flex-grow flex justify-end items-center gap-4 text-[12px] font-bold tracking-tighter">
            <Link
              to={"#"}
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px]"
            >
              BOOK&MANAGE
            </Link>
            <Link
              to={"#"}
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px]"
            >
              EXPERIENCE
            </Link>
            <Link
              to={"#"}
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px]"
            >
              DEALS&DESTINATIONS
            </Link>
            <Link
              to={"/miles-and-smiles"}
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px]"
            >
              MILES&SMILES
            </Link>
            <button
              onClick={openModal}
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px]"
            >
              HELP
            </button>
            <Link
              to={"/usersignup"}
              href="usersignup"
              className="flex items-center px-2 hover:border-b-2 hover:border-b-red-500 hover:bg-gray-900 h-[70px] font-thin"
            >
              SIGN UP
            </Link>
            <button className="bg-transparent flex items-center text-white border border-white px-4 py-2 rounded-full hover:bg-white hover:text-gray-800 transition">
              SIGN IN
            </button>
          </nav>
        </div>
      </header>

      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded shadow-lg w-1/2">
            <h2 className="text-2xl font-bold mb-4">Help Information</h2>
            <p className="mb-4">
              Here you can add information that will help users.
            </p>
            <button
              onClick={closeModal}
              className="bg-purple-800 text-white px-4 py-2 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
