import React, { useState } from "react";
import { Form, Input, DatePicker, Button, message } from "antd";
import axios from "axios";

const AddFlightPage = () => {
  const [form] = Form.useForm();

  const onFinish = async (values) => {
    const formattedValues = {
      ...values,
      flightDate: values.flightDate.format("YYYY-MM-DD"),
    };

    try {
      await axios.post(
        "http://localhost:4000/api/flights/add-flight",
        formattedValues
      );
      message.success("Flight added successfully!");
      form.resetFields();
    } catch (error) {
      message.error("Failed to add flight. Please try again.");
    }
  };

  return (
    <Form form={form} name="add-flight" onFinish={onFinish} layout="vertical">
      <Form.Item
        name="flightDate"
        label="Flight Date"
        rules={[{ required: true, message: "Please select flight date!" }]}
      >
        <DatePicker />
      </Form.Item>
      <Form.Item
        name="flightCode"
        label="Flight Code"
        rules={[{ required: true, message: "Please enter flight code!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="milesPoints"
        label="Miles Points"
        rules={[{ required: true, message: "Please enter miles points!" }]}
      >
        <Input type="number" />
      </Form.Item>
      <Form.Item
        name="capacity"
        label="Capacity"
        rules={[{ required: true, message: "Please enter capacity!" }]}
      >
        <Input type="number" />
      </Form.Item>
      <Form.Item
        name="from"
        label="From"
        rules={[
          { required: true, message: "Please enter departure location!" },
        ]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="to"
        label="To"
        rules={[{ required: true, message: "Please enter arrival location!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="flightDuration"
        label="Flight Duration"
        rules={[{ required: true, message: "Please enter flight duration!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="operatedBy"
        label="Operated By"
        rules={[{ required: true, message: "Please enter operated by!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="economyPrice"
        label="Economy Price"
        rules={[{ required: true, message: "Please enter economy price!" }]}
      >
        <Input type="number" />
      </Form.Item>
      <Form.Item
        name="businessPrice"
        label="Business Price"
        rules={[{ required: true, message: "Please enter business price!" }]}
      >
        <Input type="number" />
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit">
          Save
        </Button>
      </Form.Item>
    </Form>
  );
};

export default AddFlightPage;
