import React, { useState } from "react";

const AddFlight = () => {
  const [flightData, setFlightData] = useState({
    flightDate: "",
    flightCode: "",
    milesPoints: "",
    capacity: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFlightData({
      ...flightData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        "http://localhost:4000/api/flights/add-flight",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(flightData),
        }
      );
      const data = await response.json();
      if (response.ok) {
        setMessage(`Flight ${flightData.flightCode} added successfully!`);
        setFlightData({
          flightDate: "",
          flightCode: "",
          milesPoints: "",
          capacity: "",
        });
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage("An error occurred while adding the flight.");
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-center">Add Flight</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Flight Date:</label>
          <input
            type="date"
            name="flightDate"
            value={flightData.flightDate}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Flight Code:</label>
          <input
            type="text"
            name="flightCode"
            value={flightData.flightCode}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Miles Points:</label>
          <input
            type="number"
            name="milesPoints"
            value={flightData.milesPoints}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Capacity:</label>
          <input
            type="number"
            name="capacity"
            value={flightData.capacity}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          Save
        </button>
      </form>
      {message && (
        <div className="mt-4 text-center">
          <p className="text-red-500">{message}</p>
        </div>
      )}
    </div>
  );
};

export default AddFlight;
