import React, { useState } from "react";
import background from "../images/bg-image.jpeg";
import { Button, Radio, Input, DatePicker, InputNumber } from "antd";

const { RangePicker } = DatePicker;

const MainPage = () => {
  const [flightType, setFlightType] = useState("roundtrip");
  const [passengers, setPassengers] = useState(1);

  const handleFlightTypeChange = (e) => {
    setFlightType(e.target.value);
  };

  const handlePassengerChange = (value) => {
    setPassengers(value);
  };

  return (
    <div
      className="h-[400px] bg-contain"
      style={{ backgroundImage: `url(${background})` }}
    >
      <div className="container mx-auto px-4 py-40">
        <h1 className="text-2xl font-bold text-white">HELLO</h1>
        <h2 className="text-4xl font-bold text-white mb-8">
          Where do you want to explore?
        </h2>
        <div className="bg-white rounded-lg shadow-2xl p-8">
          <div className="flex justify-start mb-4 space-x-4">
            <Button type="primary" danger>
              Flight
            </Button>
            <Button type="primary" danger>
              Check-in / Manage booking
            </Button>
            <Button type="primary" danger>
              Flight status
            </Button>
          </div>
          <div className="flex justify-start items-center gap-5 mb-4">
            <Radio.Group onChange={handleFlightTypeChange} value={flightType}>
              <Radio value="roundtrip">Round Trip</Radio>
              <Radio value="oneway">One Way</Radio>
            </Radio.Group>
            <label className="flex items-center space-x-2">
              <input type="checkbox" />
              <span className="text-sm">
                Award ticket - Buy a ticket with Miles
              </span>
            </label>
          </div>
          <div className="flex flex-col-1 md:grid-cols-4 gap-3 mb-4">
            <Input
              className="max-w-10 p-2 border border-gray-300 rounded bg-gray-200"
              placeholder="From"
            />
            <Input
              className="w-10 p-2 border border-gray-300 rounded bg-gray-200"
              placeholder="To"
            />
            {flightType === "roundtrip" ? (
              <RangePicker className="p-2 border border-gray-300 rounded bg-gray-200 cursor-pointer" />
            ) : (
              <DatePicker className="p-2 border border-gray-300 rounded bg-gray-200 cursor-pointer" />
            )}
            <div className="relative">
              <InputNumber
                min={1}
                max={10}
                value={passengers}
                onChange={handlePassengerChange}
                className="p-2 border border-gray-300 bg-gray-200 rounded"
              />
              <div className="absolute inset-1 flex items-center pointer-events-none px-3 text-gray-500">
                {passengers === 1 ? "Passenger" : "Passengers"}
              </div>
            </div>
          </div>
          <div className="flex justify-end">
            <Button type="primary" Primary Button>
              Search flights
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
