import React, { useState } from "react";
import Header from "../components/header";

function SignupPage() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
    userType: "user", // Varsayılan değer 'user'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Backend API'ye POST isteği gönderme
    try {
      const response = await fetch("http://localhost:3000/api/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log("Response:", data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <Header />
      <div className="mx-auto max-w-lg border rounded-lg p-8 mt-10 border-gray-400">
        <h1 className="text-2xl mb-5 text-center font-bold underline underline-offset-8">
          Miles & Smiles Membership
        </h1>
        <form onSubmit={handleSubmit} className="py-5">
          <div className="mb-4">
            <label className=" text-gray-700" htmlFor="username">
              Username:
            </label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              className="max-w-lg px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700" htmlFor="password">
              Password:
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700" htmlFor="email">
              Email:
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border rounded"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700" htmlFor="userType">
              User Type:
            </label>
            <select
              id="userType"
              name="userType"
              value={formData.userType}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded"
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button
            type="submit"
            className="bg-purple-800 mt-5 text-center text-white py-2 px-3 rounded-md custom-shadow custom-shadow:hover button:active"
          >
            Create your Miles&Smiles Account
          </button>
        </form>
      </div>
    </div>
  );
}

export default SignupPage;
